    var defStr = ggbApplet.getDefinitionString(name);
    var nameConnectedObject = defStr.match(/\((.*)\)/);
    var typeConnectedObject = ggbApplet.getObjectType(nameConnectedObject);
    if(typeConnectedObject ==="numeric"){
        ggbApplet.evalCommand("SetValue(" + nameConnectedObject[1] + ",?)");
    }
}
